/**
 * @providesModule RNSFSpeechRecognizer
 * @flow
 */
'use strict';

import {
  NativeModules,
  NativeAppEventEmitter,
  DeviceEventEmitter,
  processColor
} from 'react-native';
const NativeRNSFSpeechRecognizer = NativeModules.RNSFSpeechRecognizer;

/**
 * High-level docs for the RNSFSpeechRecognizer iOS API can be written here.
 */

const RNSFSpeechRecognizer = {
  isMicrophonePermissionGranted: function(callback) {
    NativeRNSFSpeechRecognizer.isMicrophonePermissionGranted(callback);
  },

  isMicrophonePermissionDenied: function(callback) {
    NativeRNSFSpeechRecognizer.isMicrophonePermissionDenied(callback);
  },

  requestMicrophonePermission: function(callback) {
    NativeRNSFSpeechRecognizer.requestMicrophonePermission(callback);
  },
};

module.exports = RNSFSpeechRecognizer;
