#import "RNSFSpeechRecognizer.h"
#import <SpeechRecognitionManager/SpeechRecognitionManager.h>

@implementation RNSFSpeechRecognizer

RCT_EXPORT_MODULE()

RCT_EXPORT_METHOD(echo:(NSString *)input callback:(RCTResponseSenderBlock)callback)
{
    SpeechRecognitionManager *manager = [[SpeechRecognitionManager alloc] init];
    NSString *managerResponse = [manager echo: input];
    callback(@[managerResponse]);
}

@end
